    public static string Get(string name, int size = 16, string? cssClass = null)
    {
        if (!IconPaths.TryGetValue(name, out var path))
        {
            return string.Empty;
        }

        var classAttr = !string.IsNullOrEmpty(cssClass) ? $" class='{cssClass}'" : "";

        // If the path doesn't contain '<path' tags, wrap it in a path element
        var pathContent = path.Contains("<path") ? path : $"<path d='{path}'/>";

        return $@"<svg xmlns='http://www.w3.org/2000/svg' width='{size}' height='{size}' fill='currentColor'{classAttr} viewBox='0 0 16 16'>{pathContent}</svg>";
    }

    public static bool Exists(string name) => IconPaths.ContainsKey(name);
}
